-- パートボイスを再生する
--  characterName:string = ボイスを再生するキャラクター名
--  voiceName:string = 再生する感嘆符名
function PlayPartVoice(characterName, voiceName)
	if type(characterName) ~= "string" then
		debug_print("キャラクター名が文字列でない")
        return
	elseif type(voiceName) ~= "string" then
		debug_print("感嘆符名が文字列でない")
        return
    end

	local characterId = partVoiceTable[characterName]["id"]
	local voiceId = partVoiceTable[characterName][voiceName]

	if characterId == nil then
		debug_print("キャラクター名がテーブル未定義")
        return
	elseif voiceId == nil then
		debug_print("感嘆符名がテーブル未定義")
        return
	end

	local messageTag = "VO_"..characterId.."_exclamation_"..voiceId
	voice_play(messageTag)
end

-- パートボイスを番号指定で直接再生する
--  characterName:string = ボイスを再生するキャラクター名
--  voiceNumber:string = 再生するボイス番号
function PlayPartVoiceDirect(characterName, voiceNumber)
    if type(characterName) ~= "string" then
		debug_print("キャラクター名が文字列でない")
        return
	elseif type(voiceNumber) ~= "string" then
		debug_print("ボイス番号が文字列でない")
        return
    end

    local characterId = ""
    if characterName == "Player" then
        characterId = system.GetPlayerModelInfo("CharacterId")
    else
        local characterTable = partVoiceTable[characterName]
        if characterTable == nil then
            debug_print("キャラクターがテーブル未定義")
            return
        end
        characterId = characterTable["id"]
    end
    debug_print("characterId: "..characterId)

    local messageTag = "VO_"..characterId.."_exclamation_"..voiceNumber
    voice_play(messageTag)
end

-- 現代編主人公の呼びかけボイスを再生する
--  voiceId:string = 再生する呼びかけボイスのID
function PlayPlayerCallVoice(voiceId)
    if type(voiceId) ~= "string" then
        debug_print("ボイスIDが文字列でない")
        return
    end
    
    local characterId = system.GetPlayerModelInfo("CharacterId")
    local messageTag = "VO_"..characterId.."_"..voiceId
    voice_play(messageTag)
end
